INPUT;
//
// $input = <<<INPUT
//      |          
//      |  +--+    
//      A  |  C    
//  F---|--|-E---+ 
//      |  |  |  D 
//      +B-+  +--+ 
// INPUT;

$in_grid = explode("\n", $input);
$grid = [];
$max_rows = count($in_grid);
foreach ($in_grid as $row => $cols) {
  $grid[$row] = str_split($cols);
}
$max_cols = count($grid[0]);
$row = 0;
$col = array_search('|', $grid[$row]);
// Use compass directions: N, S, E, W.
$current_dir = 'S';

$answer = '';
$step = 0;
do {
  $current_val = $grid[$row][$col];
  if ($current_val == ' ') {
    break;
  }
  $step++;
  if ($current_val == '+') {
    // Change directions.
    if ($current_dir == 'N' || $current_dir == 'S') {
      if (preg_match('/[a-zA-Z\-]/', $grid[$row][$col - 1])) {
        $current_dir = 'W';
      }
      else {
        $current_dir = 'E';
      }
    }
    else {
      if (preg_match('/[a-zA-Z\|]/', $grid[$row - 1][$col])) {
        $current_dir = 'N';
      }
      else {
        $current_dir = 'S';
      }
    }
  }
  elseif (preg_match('/[a-zA-Z]/', $current_val)) {
    $answer .= $current_val;
  }
  if ($current_dir == 'N') {
    $row--;
  }
  elseif ($current_dir == 'S') {
    $row++;
  }
  elseif ($current_dir == 'E') {
    $col++;
  }
  elseif ($current_dir == 'W') {
    $col--;
  }
} while (0 <= $row && $row < $max_rows && 0 <= $col && $col < $max_cols);

print "$answer\n$step steps\n";
